<script>
  export default {
    name: 'supersymmetry',

    methods: {
      scrollMeTo(refName) {
        var element = this.$refs[refName];
        var top = element.offsetTop;

        window.scrollTo(0, top);
      }
    },

    mounted() {
      let scrollpos = window.scrollY
      let lastScrollTop = 0
      let card = document.querySelector('.card')
      let vacation = document.querySelector('.vacation')
      let vacation__trigger = document.querySelector('.vacation__trigger')
      let content = document.querySelector('.content')
      let overlay = document.querySelector('.overlay')

      vacation__trigger.addEventListener('click', () => {
        vacation.classList.add('vacation__show')
      })

      overlay.addEventListener('click', () => {
        vacation.classList.remove('vacation__show')
      })

      content.addEventListener('click', () => {
        vacation.classList.remove('vacation__show')
      })

      let hidden = () => card.classList.add("hide").classList.remove('show')
      let show = () => card.classList.add("show").classList.remove('hide')

      window.addEventListener("scroll", function() {
        let st = window.pageYOffset || document.documentElement.scrollTop;

         if (st > lastScrollTop){
             card.classList.remove('move')
         } else {
            card.classList.add('move')
         }
         lastScrollTop = st;

        scrollpos = window.scrollY;
        if (scrollpos <= 800) {
          card.classList.add("hide")
        }
        else {
          card.classList.remove("hide")
          card.classList.add("show")
        }
      }, false);
    }
  }
</script>
